<template>
  <canvas ref="canvasViewer" width="500" height="500"></canvas>
</template>

<script>
export default {
  name: "DrawingViewer",

  data: () => ({
    canvas: null,
    mockImg: 'data:image/png;base64,'
  }),

  methods: {
    setDimensions ({ width, height }) {
      this.$refs.canvasViewer.width = width
      this.$refs.canvasViewer.height = height
    },
    drawDataURIOnCanvas (strDataURI) {
      const img = new Image();
      img.addEventListener("load", () => {
        this.$refs.canvasViewer
          .getContext("2d")
          .drawImage(img, 0, 0);
      });
      img.setAttribute("src", strDataURI);
    }
  },

  mounted() {
    this.drawDataURIOnCanvas(this.mockImg)
  }
}
</script>

